const Cart = ({ id, quantity }) => {
  const { closeCart, cartItems, addItem } = useShoppingCart();
  const { removeFromCart } = useShoppingCart();
  const item = Cart.find((i) => i.id === id);
  if (item == null) return null;

  const handleRemoveFromCart = () => {
    removeFromCart(id);
  };

  return (
    <div>
      <div className="mt-8">
        <div className="flow-root">
          <ul role="list" className="-my-6 divide-y divide-gray-200">
            {cartItems.map((item) => (
              <li key={item.id} className="flex py-6">
                <div className="h-24 w-24 flex-shrink-0 overflow-hidden rounded-md border border-gray-200 mr-4">
                  <img
                    src={item.src}
                    alt={item.imageAlt}
                    className="h-full w-full object-cover object-center ml-5"
                  />
                </div>
                <div className="ml-4 flex flex-1 flex-col">
                  <div>
                    <div className="md:flex justify-evenly text-base font-medium text-gray-900">
                      <h3 className="flex justify-evenly">
                        <a href={item.href}>{item.name}</a>
                      </h3>
                      <p className="ml-4 flex justify-evenly">{item.price}</p>
                    </div>
                  </div>
                  <div className="flex flex-1 items-end justify-evenly text-sm">
                    <p className="text-gray-500">Qty {item.quantity}</p>
                    <button
                      onClick={handleRemoveFromCart}
                      className="text-gray-500 text-sm p-0.5 ml-2"
                    >
                      ازالة
                    </button>
                    <button
                      onClick={() => addItem(id, quantity)}
                      className="text-gray-500 text-sm p-0.5 ml-2"
                    >
                      أضف إلى السلة
                    </button>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div className="border-t border-gray-200 px-4 py-6 sm:px-6 z-10">
        <div className="flex justify-between text-base font-medium text-gray-900">
          المبلغ الاجمالي {" "}
          {(
            cartItems.reduce((total, cartItem) => {
              const item = Cart.find((i) => i.id === cartItem.id);
              return total + (item?.price || 0) * cartItem.quantity;
            }, 0)
          )}
        </div>
        <p className="mt-0.5 text-sm text-gray-500">Shipping and taxes calculated at checkout.</p>
        <div className="mt-6">
          <a
            href="#"
            className="btn-c flex items-center justify-center rounded-md border border-transparent bg-indigo-600 px-6 py-3 text-base font-medium text-white shadow-sm hover:bg-indigo-700"
          >
            إنتقل للتوصيل والدفع
          </a>
        </div>
      </div>
    </div>
  );
};
