import React, { Component } from "react";

class Game extends Component {
  constructor(props) {
    super(props);

    this.state = {
      choice: "",
      results: [],
      winner: "",
      score: 0,
    };
  }

  choose = (choice) => {
    this.setState({
      choice,
      results: [this.state.choice, this.aiChoose()],
      winner: this.isWinner(this.state.results),
    });
  }

  aiChoose() {
    const rand = Math.floor(Math.random() * CHOICES.length);
    return CHOICES[rand];
  }

  isWinner(results) {
    return results[0].beats === results[1].name;
  }

  keepScore(point) {
    this.setState({
      score: this.state.score + point,
    });
  }

  render() {
    return (
      <div>
        <div className="game">
          <button
            className="choice-btn"
            onClick={() => this.choose("rock")}
          >
            Rock
          </button>
          <button
            className="choice-btn"
            onClick={() => this.choose("scissors")}
          >
            Scissors
          </button>
          <button
            className="choice-btn"
            onClick={() => this.choose("paper")}
          >
            Paper
          </button>
        </div>
        <div className="results">
          {this.state.results.map((result, idx) => (
            <div className="results__result">
              <img src="img/icon-{result.name}.svg" alt={result.name} />
            </div>
          ))}
        </div>
        <div className="results__winner">
          {this.state.winner}
        </div>
        <div className="results__text">
          Your score: {this.state.score}
        </div>
        <button className="play-again">Play Again</button>
      </div>
    );
  }
}

const App = () => {
  return <Game />;
};

export default App;
