import React, { useState } from "react";
import "./styles.css";

const CHOICES = [
  {
    name: "paper",
    beats: "rock",
  },
  {
    name: "scissors",
    beats: "paper",
  },
  {
    name: "rock",
    beats: "scissors",
  },
];

const App = () => {
  const [choice, setChoice] = useState("");
  const [result, setResult] = useState("");
  const [score, setScore] = useState(0);

  const handleChoose = () => {
    const aiChoice = CHOICES[Math.floor(Math.random() * CHOICES.length)];
    const results = [choice, aiChoice];

    setResult(results.join(" "));
    setScore(
      isWinner(results) ? score + 1 : isLoser(results) ? score - 1 : score
    );
  };

  const isWinner = (results) => results[0].beats === results[1].name;
  const isLoser = (results) => results[1].beats === results[0].name;

  return (
    <div className="app">
      <h1>Rock Paper Scissors</h1>
      <div className="choices">
        <button onClick={() => setChoice("rock")}>Rock</button>
        <button onClick={() => setChoice("paper")}>Paper</button>
        <button onClick={() => setChoice("scissors")}>Scissors</button>
      </div>
      <div className="result">
        <p>{result}</p>
        <p>Score: {score}</p>
      </div>
      <button onClick={handleChoose}>Play</button>
    </div>
  );
};

export default App;
