/* eslint-disable jsx-a11y/no-redundant-roles */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React from 'react'
/*
  This example requires some changes to your config:

  ```
  // tailwind.config.js
  module.exports = {
    // ...
    theme: {
      extend: {
        gridTemplateRows: {
          '[auto,auto,1fr]': 'auto auto 1fr',
        },
      },
    },
    plugins: [
      // ...
      require('@tailwindcss/aspect-ratio'),
    ],
  }
  ```
*/
import { StarIcon } from '@heroicons/react/20/solid'

const product = {
    name: 'Green Flower',
    price: '193.00 ر.س',
    href: '',
    breadcrumbs: [
        { id: 1, name: 'Men', href: '#' },
        { id: 2, name: 'Clothing', href: '#' },
    ],
    images: [
        {
            src: 'https://media.zid.store/bda63855-f7ff-4be7-bd9f-1d497a22336c/edf0a930-e5d6-46b7-b6c2-4facff4fbb7f.jpg',
            alt: 'Two each of gray, white, and black shirts laying flat.',
        },
        {
            src: 'https://media.zid.store/bda63855-f7ff-4be7-bd9f-1d497a22336c/281608d4-2d5a-44bb-9973-5f437a8f6c77.jpg',
            alt: 'Model wearing plain black basic tee.',
        },
        {
            src: 'https://media.zid.store/bda63855-f7ff-4be7-bd9f-1d497a22336c/7eef0b11-2eaf-4495-990c-d35de12580ee.png',
            alt: 'Model wearing plain gray basic tee.',
        },
        {
            src: 'https://media.zid.store/bda63855-f7ff-4be7-bd9f-1d497a22336c/f781efce-7be4-4894-8b34-0b29ff7e297b.png?_ga=2.227989659.707408916.1694256975-812482167.1694256975',
            alt: 'Model wearing plain white basic tee.',
        },
    ],
    description:
        '',
    highlights: [

    ],
    details:
        '',
}
const reviews = { href: '#', average: 4, totalCount: 117 }

function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
}

const CardDetails = () => {
    handleMinusClick = () => {
        this.setState({
          quantity: this.state.quantity - 1,
        });
      };

      handlePlusClick = () => {
        this.setState({
          quantity: this.state.quantity + 1,
        });
      };
    return (
        <div>
            <div className="bg-h">
                <div className="pt-6">
                    <nav aria-label="Breadcrumb">
                        <ol role="list" className="mx-auto flex max-w-2xl items-center space-x-2 px-4 sm:px-6 lg:max-w-7xl lg:px-8">
                            {product.breadcrumbs.map((breadcrumb) => (
                                <li key={breadcrumb.id}>
                                    <div className="flex items-center">
                                        <a href={breadcrumb.href} className="mr-2 text-sm font-medium text-gray-900">
                                            {breadcrumb.name}
                                        </a>
                                        <svg
                                            width={16}
                                            height={20}
                                            viewBox="0 0 16 20"
                                            fill="currentColor"
                                            aria-hidden="true"
                                            className="h-5 w-4 text-gray-300"
                                        >
                                            <path d="M5.697 4.34L8.98 16.532h1.327L7.025 4.341H5.697z" />
                                        </svg>
                                    </div>
                                </li>
                            ))}
                            <li className="text-sm">
                                <a href={product.href} aria-current="page" className="font-medium text-gray-500 hover:text-gray-600">
                                    {product.name}
                                </a>
                            </li>
                        </ol>
                    </nav>
                    {/* Image gallery */}
                    <div className="mx-auto mt-6 max-w-2xl sm:px-6 d-s lg:grid lg:max-w-7xl lg:grid-cols-3 lg:gap-x-8 lg:px-8">
                        {/* Product info */}
                        <div className="w-g1 md:block aspect-w-4 lg:aspect-h-4 lg:aspect-w-3 sm:overflow-hidden sm:rounded-lg">
                            <img
                                src={product.images[3].src}
                                alt={product.images[3].alt}
                                className="w-h object-cover object-center"
                            />
                        </div>
                        <div className="mx-auto text-center max-w-2xl px-4 pb-16 pt-10 sm:px-6 lg:px-8 lg:pb-24 lg:pt-16">
                            <div className='bg-white p-10'>
                                <div className="lg:col-span-2 lg:border-r lg:border-gray-200 lg:pr-8">
                                    <h1 className="text-2xl font-bold tracking-tight text-gray-900 sm:text-3xl">{product.name}</h1>
                                </div>
                                {/* Options */}
                                <div className="mt-4 lg:row-span-3 lg:mt-0">
                                    <h2 className="sr-only">Product information</h2>
                                    <p className="text-3xl tracking-tight text-gray-900">{product.price}</p>
                                    {/* Reviews */}
                                    <div className="mt-6 flex justify-center">
                                        <h3 className="sr-only">Reviews</h3>
                                        <div className="flex items-center">
                                            <div className="flex items-center">
                                                {[0, 1, 2, 3, 4].map((rating) => (
                                                    <StarIcon
                                                        key={rating}
                                                        className={classNames(
                                                            reviews.average > rating ? 'text-gray-900' : 'text-gray-200',
                                                            'h-5 w-5 flex-shrink-0'
                                                        )}
                                                        aria-hidden="true"
                                                    />
                                                ))}
                                            </div>
                                            <p className="sr-only">{reviews.average} out of 5 stars</p>
                                            <a href={reviews.href} className="ml-3 text-sm font-medium text-indigo-600 hover:text-indigo-500">
                                                {reviews.totalCount} reviews
                                            </a>
                                        </div>
                                    </div>
                                    <form className="mt-10">
                                    <div className="form-group">
        <div className="quantity">
          <button
            type="button"
            className="btn-quantity minus"
            onClick={this.handleMinusClick}
          >
            <i className="fa-solid fa-window-minimize"></i>
          </button>
          <label className="screen-reader-text">
            Green Flower
          </label>
          <input
            type="number"
            id="product-quantity"
            className="input-text qty text"
            step="1"
            min="1"
            max="5"
            name="quantity"
            value={this.state.quantity}
            title="Qty"
            size="4"
            placeholder=""
            inputmode="numeric"
            onChange={(e) => this.setState({ quantity: e.target.value })}
          />
          <button
            type="button"
            className="btn-quantity plus"
            onClick={this.handlePlusClick}
          >
            <i className="fa-solid fa-plus"></i>
          </button>
        </div>
      </div>
                                        <button
                                            type="submit"
                                            className="mt-10 flex w-full items-center justify-center rounded-md border border-transparent bg-ad px-8 py-3 text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                                        >
                                            أضف للسلة
                                        </button>
                                    </form>
                                </div>
                                <div className="py-10 lg:col-span-2 lg:col-start-1 lg:border-r lg:border-gray-200 lg:pb-16 lg:pr-8 lg:pt-6">
                                    {/* Description and details */}

                                </div>
                            </div>
                            <div className="aspect-h-4 aspect-w-3 overflow-hidden rounded-lg">
                                <img
                                    src={product.images[0].src}
                                    alt={product.images[0].alt}
                                    className="w-d object-cover object-center"
