import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const fetchProducts = createAsyncThunk("productsilce/fetchProducts", async () => {
  const res = await fetch("/Product.js");
  const data = await res.json();
  return data;
});

const productsilce = createSlice({
  initialState: [],
  name: "productsilce",
  reducers: {
    // ...
  },
  extraReducers: (builder) => {
    builder.addCase(fetchProducts.fulfilled, (state, action) => {
      const products = action.payload.map((product) => ({
        id: product.id,
        name: product.name,
        price: product.price,
      }));
      return products;
    });
  },
});

export const { } = productsilce.actions;
export default productsilce.reducer;
